import "./Carousel.css";
import strawberryImage from "../../Assets/Images/fruit_strawberry.png";
import avocadoImage from "../../Assets/Images/fruit_avocado.png";
import orangeImage from "../../Assets/Images/fruit_orange.png";
import { useRef, useState } from "react";

function Carousel(): JSX.Element {
  const [active, setActive] = useState(0);  
  const [leftMockup, setLeftMockup] = useState(0); 
  const [items, setItems] = useState([
    { image: strawberryImage, name: "strawberry" },
    { image: avocadoImage, name: "avocado" },
    { image: orangeImage, name: "orange" }
  ]);

  const handleNext = () => {
    setActive((prev) => (prev + 1) % items.length); 
    setLeftMockup((prev) => prev + 100 / (items.length - 1)); 
  };

  const handlePrev = () => {
    setActive((prev) => (prev - 1 + items.length) % items.length); // Cycles backwards
    setLeftMockup((prev) => prev - 100 / (items.length - 1)); // Moves the mockup
  };

  return (
    <div className="Carousel">
      <div className="list">
        {items.map((item, index) => (
          <div
            key={index}
            className={`item ${item.name} ${active === index ? "active" : ""}`}
            style={{
              transform: `translateX(${(index - active) * 100}%)`, // This moves the items
              transition: "transform 0.5s ease",
            }}
          >
            <div className="content">{item.name}</div>
            <img src={item.image} className="fruit" alt={item.name} />
          </div>
        ))}
      </div>

      <div className="leaves"></div>
      <div className="mockup" style={{ left: `${leftMockup}%` }}></div>
      <div className="shadow"></div>

      <div className="arrow">
        <button id="prev" onClick={handlePrev}>
          &lt;
        </button>
        <button id="next" onClick={handleNext}>
          &gt;
        </button>
      </div>
    </div>
  );
}

export default Carousel;
