import "./Carousel.css";
import strawberryImage from "../../Assets/Images/fruit_strawberry.png";
import avocadoImage from "../../Assets/Images/fruit_avocado.png";
import orangeImage from "../../Assets/Images/fruit_orange.png";
import { useRef, useState } from "react";

function Carousel(): JSX.Element {
  let list = useRef([]);
  let carousel = useRef();
  let prev = useRef();
  let next = useRef();
  let mockup = useRef();
  let hidden_old = useRef();


  let [count, setCount] = useState(list.current.length);
  let [active, setActive] = useState(0);
  let [leftMockup, setLeftMockup] = useState(0);
  let [left_each_item, setLeft_each_item] = useState(100 / (list.current.length - 1));

  function handleNext() {
    setActive(active >= count - 1 ? 0 : active + 1);
    setLeftMockup(leftMockup + left_each_item)
    changeCarousel()
  }

  function handlePrev() {
    setActive(active <= 0 ? count - 1 : active -1);
    setLeftMockup(leftMockup - left_each_item)
    changeCarousel()
  }

   function changeCarousel(){

  }

  const setRef = (el: HTMLDivElement, index: number) => {
    if (el) {
      list.current[index] = el;
    }
  };

  return (
    <div className="Carousel" ref={carousel}>
      <div className="list">
        <div className="item strawberry active" ref={el => setRef(el, 0)}>
          <div className="content">strawberry</div>
          <img src={strawberryImage} className="fruit" alt="strawberry" />
        </div>
        <div className="item avocado" ref={el => setRef(el, 1)}>
          <div className="content">avocado</div>
          <img src={avocadoImage} className="fruit" alt="avocado" />
        </div>
        <div className="item orange hidden" ref={el => setRef(el, 2)} ref >
          <div className="content">orange</div>
          <img src={orangeImage} className="fruit" alt="orange" />
        </div>
      </div>
      <div className="leaves"></div>
      <div className="mockup" ref={mockup}></div>
      <div className="shadow"></div>
      <div className="arrow">
        <button id="prev" ref={prev} onClick={handlePrev}>
          &lt;
        </button>
        <button id="next" ref={next} onClick={handleNext}>
          &gt;
        </button>
      </div>
    </div>
  );
}

export default Carousel;
